import React, { Component } from "react";

import { TraderList } from "./components/trader-list/trader-list";

import "./App.css";

class App extends Component {
  constructor() {
    super();

    this.state = { traders: [],
                   searchField: ''};
  }

  componentDidMount() {
    fetch("https://randomuser.me/api/?results=12")
      .then(response => response.json())
      .then(users => this.setState({ traders: users.results }));
  }

  render() {
    const { traders, searchField } = this.state;
    const filteredTraders = traders.filter(trader => trader.name.toLowerCase().includes(searchField.toLowerCase()));
    return (
      <div className="App">
        <input
          type="search"
          placeholder="search traders"
          onChange={event => this.setState({ searchField: event.target.value })}
        />
      <TraderList traders={this.filteredTraders} />
      </div>
    );
  }
}

export default App;
